//map 함수 사용할 때 : 박스 별로 키 값을 부여->고유한 값으로 식별 가능
import React, {useEffect, useState} from 'react';
import detail from "../data/productDetail.js"
import "../css/Boxb.css"
import LikeButton from "../components/LikeButton";
import {useNavigate, Link} from 'react-router-dom';
import axios from 'axios';

import {
    StyledBox,
    TextContainer,
    Button,
} from "../styled"

const Boxb = () => {
    const navigate = useNavigate();
    const handleClick =(url) => {
        navigate(url);
    };

    const [productList, setProductList] = useState([]);

    useEffect(()=>{
        axios.get("https://693859ae-ca59-4e38-a01d-de33a060a89a.mock.pstmn.io/session5")
        .then(response=>{
            setProductList(response.data);

        })
    })

    console.log(productList.products);

    return <>{productList.map((product)=>(
        <>

            <StyledBox key={product.id}>
                <Link to= "" ><img src={product.img} alt="no imag"></img></Link>

                    <textitem>
                        <p><b>{product.title}</b></p>
                        <p className="address"> {product.address}</p>
                        <TextContainer>
                            <p className="price"> {product.price}</p>
                            <LikeButton />
                        </TextContainer>

                        {/* 상세보기 버튼 */}
                        <Button onClick={()=> handleClick('/detail/' + product.id)}> 상세보기 </Button> 
                    </textitem>

            </StyledBox>

        </>
        ))}
        </>

};

export default Boxb
