import styled from "styled-components"

//App.js
export const StyledApp=styled.div`
display:flex;
justify-content:center;
`;

export const StyledCard=styled.div`
width: 100vh;
`;

export const StyledHeader=styled.div`
display:flex;
justify-content: space-between;


`

//Boxb.js
export const StyledBox = styled.button`
border: 1px solid transparent;
width: 30vh;
height: 30vh;
font-size : 12px;
background-color: white;
text-align: left;
margin: 0vh 2vh 2vh 0vh;
`;

export const TextContainer = styled.div`
display: flex;
justify-content: space-between;
`

export const Button = styled.div`
background-color: grey;
width: 25%;
text-align: center;
color: white;
`

//ProductDetail.js
export const ProdDetCon=styled.div`
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
height: 100vh;
`

export const ProdName = styled.div`
font-size: 20px;
font-weight: 900;
margin: 0 0 20px 0;
`
export const ProdContainer = styled.div`
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;

padding: 40px;
width: 40%;
background-color: #ffefdb;
border-radius: 10%;
`

export const ProdDesc = styled.div`
display: flex;
flex-direction: column;

`
export const AboutDetail = styled.div`
display: flex;
margin: 5px;

`

export const SearchContainer=styled.div`
display: flex;
padding: 5px;

`
