import React from 'react'
import detail from '../data/productDetail'
import {useParams} from 'react-router-dom';
import "../css/Detail.css";

import {
    ProdContainer,
    ProdName,
    ProdDesc
} from "../styled"

const ProductDetail = () => {
    //useParams()=> 현재 url 의 붙어 있는 url parameter를 인식하는 역할
    //'detailID'에 현재 url 파라미터를 담게 됌
    const {detailID} = useParams();
    // const id = detailID;

    //map함수를 사용하면 리스트에 저장된 모든 데이터 값이 출력되지만,
    //filter함수를 사용하면 상세 페이지에 해당하는 특정 데이터 값만을 선택적으로 출력가능. 
    const which = detail.detailProd.filter((one)=>one.detailID===detailID)[0];

    return (
        <div>
            <ProdContainer>
            <h4> {detailID}번 상품에 대한 상세페이지</h4>
            <img className="imgDetail" src={which.img} alt='no img'></img>
            <ProdName> {which.title} </ProdName>
            <ProdDesc>
                <h3> 가격: {which.price} </h3>
                <h3> 주소: {which.address} </h3>
                <h3> 상세 정보: {which.contents} </h3>
            </ProdDesc>



            </ProdContainer>
        </div>

    )
}

export default ProductDetail;
